import React from 'react';


import {connect} from 'react-redux';
import {selectCollectionType} from '../../redux/shop/shop.selector';
import CollectionItem from '../../components/collection-item/collection-item.component';

import './collection.styles.scss';

const CollectionPage=({collection})=>{
    console.log("********************COLLECTION PAGE**********************");
    console.log(match.params);
    console.log(match.params.collectionId);


    const {title,items}=collection;

    return(
        (
            <div className='category'>
                <h2 className='title'>{title}</h2>
            </div>
        )
    )
}


//state is overall reducer state from the top, ownProps is props of component we r wrapping in connect
const mapStateToProps=(state,ownProps)=>({
    collection:selectCollectionType(ownProps.match.params.collectionId)(state)
})


export default connect(mapStateToProps)(CollectionPage);
