import React from 'react';
import {Route} from 'react-router-dom';



import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';


const ShopPage =({match})=>{

    console.log("ShopPage -match___________________________");
    console.log(match) //get path is '/shop'
    
    
    
    return(
    //shop page is nested in a route in app.js
    //in Route to COllECTIONPAGE, the path params name u use categoryId, so when accessing it u refer to it in mapstatetoprops of 
        <div className='shop-page'>
            <Route exact path={`${match.path}`} component={CollectionsOverview} />  
            <Route path={`${match.path}/:categoryId`} component={CollectionPage} />
            
        </div>
)
}




export default ShopPage;
